<script>
export default {
  data() {
    return {
      fixCategories: this.$store.getters.fixCategories,
      processElements: this.$store.getters.processElements,
    };
  },
  computed: {
    categories() {
      const categories = this.arraySplitting(this.fixCategories, 4);
      return categories;
    },
  },
  methods: {
    arraySplitting(arr, chunk) {
      let chunkedArray = [];
      let index = 0;
      while (index < arr.length) {
        chunkedArray.push(arr.slice(index, index + chunk));
        index += chunk;
      }
      return chunkedArray;
    },
  },
  beforeRouteLeave(_, __, next) {
    if (this.$store.getters.isPhoneMenuOpen) {
      this.$store.commit("openClosePhoneMenu");
    }

    this.$store.commit("appearHiddenLoader", true);
    next();
  },
  created() {
    setTimeout(() => {
      this.$store.commit("appearHiddenLoader", false);
    }, 500);
  },
};
</script>

